function pygmentizeCode(){var e=document.getElementById("input-text").value,t=document.getElementById("lexer").value;reqwest({url:"/pygmentize",type:"json",method:"post",data:{code:e,lexer:t},error:function(){document.getElementById("output-text").innerHTML="An error occured while pygmenting your code."},success:function(e){document.getElementById("output-text").innerHTML=e.code}})}