!function(){this.pygmentizeCode=function(){var e,t;return e=document.getElementById("input-text").value,t=document.getElementById("lexer").value,reqwest({url:"/pygmentize",type:"json",method:"post",data:{code:e,lexer:t},error:function(){return document.getElementById("output-text").innerHTML="An error occured while pygmenting your code."},success:function(e){return document.getElementById("output-text").innerHTML=e.code}})},this.generateStylesheet=function(){var e;return e=document.getElementById("theme").value,reqwest({url:"/stylesheets/generate",type:"json",method:"post",data:{theme:e},error:function(){return document.getElementById("output-text").innerHTML="An error occured while generating your CSS."},success:function(e){return document.getElementById("output-text").innerHTML=e.code}})}}.call(this);